import React, { useEffect, useState } from 'react'
import { Grid, } from '@material-ui/core'
import PokeCard from './pokeCard';
import axios from 'axios';



function NestedGrid(props){
     const [pokemonData, setPokemonData] = useState([])
     const [pokemonLista, setPokemonLista] = useState([])

    useEffect(() =>{
        async function getData()  {
            const response = await axios.get(
                'https://pokeapi.co/api/v2/pokemon?limit=1&offset=0'
            );
            setPokemonData(response.data.results)
        }
        getData()


        async function getPokemon(pokemon) {
           const response = await axios.get(
                `https://pokeapi.co/api/v2/pokemon/${pokemon.name}`);

                let image = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemon.url.split('/')[pokemon.url.split('/').length - 2]}.png`
                let id = pokemon.url.split('/')[pokemon.url.split('/').length - 2]
                response.data['id'] = id
                response.data['image'] = image
                console.log(response)


                setPokemonLista([...pokemonLista,response.data])}
                // console.log('lista ', pokemonLista)

        pokemonData.map((pokemon) =>(
            getPokemon(pokemon)
        ))


    }, )

    function GridLine(){  

        function logData (data){
            props.logapp(data)
        }

        return(
            <>
            {pokemonLista.map((pokemon) =>(
                <> 
                    <Grid key={pokemon.id} item xs={2}>
                        <PokeCard 
                        name={pokemon.name} 
                        image = {`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemon.id}.png`}
                        // price = {Math.floor((Math.random() * 1000) + 1)}
                        price = {1}
                        log= {logData}
                        >
                        </PokeCard>
                    </Grid>
                </>
                )           
            )}
            </>)
   }
    return(            
        <>
            <Grid container spacing={1}>
                <Grid container item xs={12} spacing={1}>   
                    <GridLine></GridLine>
                </Grid>
            </Grid>

        </>
    )
}
export default NestedGrid;
