import React, { useEffect, useState } from 'react'
import { Grid, } from '@material-ui/core'
import PokeCard from './pokeCard';
import api from './api';



function NestedGrid(){

    const [pokemonData, setPokemonData] = useState([])

    useEffect(() =>{
        fetch('https://pokeapi.co/api/v2/pokemon/')
        .then(response => response.json())
        .then(
            (data) =>{
                setPokemonData(data)
                console.log(data)

            },
        )
    }, [])

    function GridLine(){
        return(
            <>
                <Grid item xs ={3}>
                    <PokeCard></PokeCard>
                </Grid>
                <Grid item xs ={3}>
                    <PokeCard></PokeCard>
                </Grid>
                <Grid item xs ={3}>
                    <PokeCard></PokeCard>
                </Grid>
                <Grid item xs ={3}>
                    <PokeCard></PokeCard>
                </Grid>              
            </>
        )
    }

    return(
        <>
            <Grid container spacing={1} direction='row' /* className ={classes.root} */>
                <Grid container item xs={12}>
                    <GridLine></GridLine>
                </Grid>
                <Grid container item xs={12}>
                    <GridLine></GridLine>
                </Grid>
                <Grid container item xs={12}>
                    <GridLine></GridLine>                    
                </Grid>
            </Grid>
        </>
    )
}
export default NestedGrid;
