import React from  'react'
import {Avatar, Grid, Card, CardContent,CardActions, Typography, Button, CardHeader } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles'
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart'
import { render } from '@testing-library/react'



const useStyles = makeStyles({
    root: {

    },
    card: {
        width:'100%'

    },
    cardHeader:{
        fontSize: '40px',
        textAlign:'center'
    },


  })

let price = 0
let clear = false
function Cart(props){

    const handleCompra = (e)=>{
        clear = true
    }
    price  += Number(props.price/2)
    const classes = useStyles()
    return(
        <>
            <Card>
                <CardHeader
                    className={classes.cardHeader}
                    title="Cart (3)"
                    avatar={
                        <Avatar>
                            <ShoppingCartIcon/>
                        </Avatar>}>                   
                </CardHeader>
                <Grid container direction='row' spacing={1} >
                    <Grid container  item xs={12} spacing={1}>
                        <Grid item xs={12}>
                            {clear ? <></> :  props.cartItem}
                        </Grid>
                    </Grid>
            </Grid>
                <CardContent>
                <Typography component="p"> <strong> Total:  </strong>{price} coins</Typography>
            </CardContent>
                <CardActions>
                <Button  variant='contained' color='primary' onClick={handleCompra()}> Buy</Button>
            </CardActions>
            </Card>
            </>)

} export default Cart;
