import React from 'react'
import { makeStyles } from '@material-ui/core/styles'

import charmander from './images/pokemon/charmander.png'
import { Card, CardMedia, CardActionArea, CardContent, Typography, CardActions, Button, List, ListItemText } from '@material-ui/core'

const useStyles = makeStyles({
    root: {
    //   maxWidth: 200,
    },
    media: {
      alignItems: 'center',
      alignText:'center',
      height: 200,
      width: 200,
      maxWidth:'100%',
      objectFit: 'contain'
    },
  })

function PokeCard( props ){
    const classes = useStyles()
    return(
    <>
        <Card className={classes.root}>
            <CardActionArea >
                    <CardMedia
                        className = {classes.media}
                        image = {props.image}
                        title = 'This is suposed to be a PokÃ©mon'/> 
                    <CardContent>
                        <List>
                            <ListItemText   component={'span'}>
                                ${props.name}
                            </ListItemText>
                        </List>
                    </CardContent>
                </CardActionArea>
                <CardActions>
                    <Button size='small' variant='contained' color='primary'>
                        Add to cart now
                    </Button>
                </CardActions>
        </Card>
    </>)

}
export default PokeCard;
