import React from 'react'
import { makeStyles } from '@material-ui/core/styles'
import { Card, CardMedia, CardActionArea, CardContent, Typography, CardActions, Button, List, ListItemText } from '@material-ui/core'

const useStyles = makeStyles({
    root: {
       maxWidth: 200,
    },
    media: {
      alignItems: 'center',
      alignText:'center',
      height: 100,
      width: 100,
      maxWidth:'100%',
      objectFit: 'contain'
    },
    content:{
        textTransform:'capitalize'
    }
  })


function PokeCard( props ){

    function handleCart(){
        this.props.logData(props.price)  
    }

    const classes = useStyles()
    return(
    <>
        <Card className={classes.root} coins={props.price}>
            <CardActionArea >
                    <CardMedia
                        className = {classes.media}
                        image = {props.image}
                        title = 'This is suposed to be a PokÃ©mon'/> 
                    <CardContent className={classes.content}> 
                        <List>
                            <ListItemText   component={'span'}>
                                {props.name}
                            </ListItemText>
                            <ListItemText   component={'span'}>
                                {`${props.price} coins`}
                            </ListItemText>
                        </List>
                    </CardContent>
                </CardActionArea>
                <CardActions>
                    <Button size='small' variant='contained' color='primary' onClick={handleCart}>
                        Add to cart now
                    </Button>
                </CardActions>
        </Card>
    </>)

}
export default PokeCard;
