    const [pokemonData, setPokemonData] = useState([]) 
    const [pokemonList,setPokemonList] = useState([])

    useEffect(() =>{
        async function getData(){
            const response = await axios.get(
                'https://pokeapi.co/api/v2/pokemon/'
