import React, { useEffect, useState } from 'react'
import { Grid, } from '@material-ui/core'
import PokeCard from './pokeCard';
import axios from 'axios';



function NestedGrid(props){
     const [pokemonData, setPokemonData] = useState([])
     const [pokemonUni, setPokemonUni] = useState([])



    useEffect(() =>{
        async function getData()  {
            const response = await axios.get(
                'https://pokeapi.co/api/v2/pokemon?limit=200&offset=0'
            );
            setPokemonData(response.data.results)
        }
        getData()

        async function getPokemon(name) {
            const response = axios.get(
                `https://pokeapi.co/api/v2/pokemon/${name}`
            ).then( res =>{
                console.log('polemon ',res.data)
                setPokemonUni([...pokemonUni, res.data])}
            )};

        pokemonData.map((pokemon)=>{
            getPokemon(pokemon.name)
        })

    }, [])

    function GridLine(){

        function logData (data){
            props.logapp(data)
            // console.log(data)
        }
        console.log(pokemonUni)
        return(
            <>
            {pokemonData.map((pokemon) =>(
                <> 
                    <Grid item key={pokemon.url.split('/')[pokemon.url.split('/').length - 2]} xs={2}>
                        <PokeCard 
                        name={pokemon.name} 
                        image = {`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemon.url.split('/')[pokemon.url.split('/').length - 2]}.png`}
                        // price = {Math.floor((Math.random() * 1000) + 1)}
                        // price = {getPokemon(pokemon.name).abilities[0].name}
                        price = {1}
                        log= {logData}

                        >
                        </PokeCard>
                    </Grid>
                </>
                )           
            )}
            </>)
   }
    return(            
        <>
            <Grid container spacing={1}>
                <Grid container item xs={12} spacing={1}>   
                    <GridLine></GridLine>
                </Grid>
            </Grid>

        </>
    )
}
export default NestedGrid;
