import React from 'react'
import { makeStyles } from '@material-ui/core/styles'

import charmander from './images/pokemon/charmander.png'
import { Card, CardMedia, CardActionArea, CardContent, Typography, CardActions, Button, List, ListItemText } from '@material-ui/core'
import jssPluginPropsSort from 'jss-plugin-props-sort'





const useStyles = makeStyles({
    root: {
    //   maxWidth: 200,
    },
    media: {
      alignItems: 'center',
      alignText:'center',
      height: 200,
      width: 200,
      maxWidth:'100%',
      objectFit: 'contain'
    },
  })

// fetch('https://pokeapi.co/api/v2/pokemon/')
// .then(response => response.json())
// .then(data => console.log(data))

function PokeCard( props ){
    const classes = useStyles()
    return(
    <>
        <Card className={classes.root}>
            <CardActionArea >
                    <CardMedia
                        className = {classes.media}
                        image = {charmander}
                        title = 'This is suposed to be a Pokémon'/> 
                    <CardContent>
                        <Typography  component="h2">
                            Pokemon's name
                        </Typography>
                        <List>
                            <ListItemText   component={'span'}>
                                {`${props.name} - Nº: ${props.number}`}
                            </ListItemText>
                            <ListItemText   component={'span'}>
                                {` HP: ${props.hp} - Attack: ${props.attack} - Deffense: ${props.deffense}`}
                            </ListItemText>
                        </List>
                    </CardContent>
                </CardActionArea>
                <CardActions>
                    <Button size='small' variant='contained' color='primary'>
                        Add to cart now
                    </Button>
                </CardActions>
        </Card>
    </>)

}
export default PokeCard;
