import React, { useEffect, useState } from 'react'
import { Grid, } from '@material-ui/core'
import PokeCard from './pokeCard';
import axios from 'axios';



function NestedGrid(props){
     const [pokemonData, setPokemonData] = useState([])
     const [pokemonUni, setPokemonUni] = useState([])



    useEffect(() =>{
        async function getData()  {
            const response = await axios.get(
                'https://pokeapi.co/api/v2/pokemon?limit=200&offset=0'
            );
            setPokemonData(response.data.results)
        }
        getData()

        async function getPokemon(pokemon) {
           const response = await axios.get(
                `https://pokeapi.co/api/v2/pokemon/${pokemon.name}`);
                console.log('polemon ',response.data.weight)

                let image = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemon.url.split('/')[pokemon.url.split('/').length - 2]}.png`
                let id = pokemon.url.split('/')[pokemon.url.split('/').length - 2]

                obj_poke = {'image': image, 'id':id}

                setPokemonUni([...pokemonUni, [...response.data, obj_poke]])}


        pokemonData.map((pokemon) =>(
            getPokemon(pokemon.name)

        ))


    }, [pokemonUni, pokemonData])

    function GridLine(){  
        function logData (data){
            props.logapp(data)
        }
        return(
            <>
            {pokemonData.map((pokemon) =>(
                <> 
                    <Grid item key={pokemon.url.split('/')[pokemon.url.split('/').length - 2]} xs={2}>
                        <PokeCard 
                        name={pokemon.name} 
                        image = {`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemon.url.split('/')[pokemon.url.split('/').length - 2]}.png`}
                        // price = {Math.floor((Math.random() * 1000) + 1)}
                        price = {1}
                        log= {logData}

                        >
                        </PokeCard>
                    </Grid>
                </>
                )           
            )}
            </>)
   }
    return(            
        <>
            <Grid container spacing={1}>
                <Grid container item xs={12} spacing={1}>   
                    <GridLine></GridLine>
                </Grid>
            </Grid>

        </>
    )
}
export default NestedGrid;
